<?php   require_once './../../../libs/saewsclient/known/functions.php';   $discapacidad =  $_REQUEST["discapacidad"];   if($discapacidad==""){      $respuesta = json_decode(readDiscapacidadAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readDiscapacidad($discapacidad));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
