<?php   require_once './../../../libs/saewsclient/known/functions.php';   $docente =  $_REQUEST["docente"];   if($docente==""){      $respuesta = json_decode(readDocenteAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readDocente($docente));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
