<?php   require_once './../../../libs/saewsclient/known/functions.php';   $docenteasignatura =  $_REQUEST["docenteasignatura"];   if($docenteasignatura==""){      $respuesta = json_decode(readDocenteAsignaturaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readDocenteAsignatura($docenteasignatura));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
