<?php   require_once './../../../libs/saewsclient/known/functions.php';   $documento =  $_REQUEST["documento"];   if($documento==""){      $respuesta = json_decode(readDocumentoAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readDocumento($documento));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
