<?php   require_once './../../../libs/saewsclient/known/functions.php';   $educacioncontinua =  $_REQUEST["educacioncontinua"];   if($educacioncontinua==""){      $respuesta = json_decode(readEducacionContinuaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readEducacionContinua($educacioncontinua));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
