<?php   require_once './../../../libs/saewsclient/known/functions.php';   $enfermedad =  $_REQUEST["enfermedad"];   if($enfermedad==""){      $respuesta = json_decode(readEnfermedadAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readEnfermedad($enfermedad));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
