<?php   require_once './../../../libs/saewsclient/known/functions.php';   $estado =  $_REQUEST["estado"];   if($estado==""){      $respuesta = json_decode(readEstadoAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readEstado($estado));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
