<?php   require_once './../../../libs/saewsclient/known/functions.php';   $estadocivil =  $_REQUEST["estadocivil"];   if($estadocivil==""){      $respuesta = json_decode(readEstadoCivilAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readEstadoCivil($estadocivil));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
