<?php   require_once './../../../libs/saewsclient/known/functions.php';   $estudiante =  $_REQUEST["estudiante"];   if($estudiante==""){      $respuesta = json_decode(readEstudianteAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readEstudiante($estudiante));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
