<?php   require_once './../../../libs/saewsclient/known/functions.php';   $experiencialaboral =  $_REQUEST["experiencialaboral"];   if($experiencialaboral==""){      $respuesta = json_decode(readExperienciaLaboralAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readExperienciaLaboral($experiencialaboral));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
