<?php   require_once './../../../libs/saewsclient/known/functions.php';   $genero =  $_REQUEST["genero"];   if($genero==""){      $respuesta = json_decode(readGeneroAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readGenero($genero));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
