<?php   require_once './../../../libs/saewsclient/known/functions.php';   $hobbies =  $_REQUEST["hobbies"];   if($hobbies==""){      $respuesta = json_decode(readHobbiesAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readHobbies($hobbies));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
