<?php   require_once './../../../libs/saewsclient/known/functions.php';   $institucion =  $_REQUEST["institucion"];   if($institucion==""){      $respuesta = json_decode(readInstitucionAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readInstitucion($institucion));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
