<?php   require_once './../../../libs/saewsclient/known/functions.php';   $jornada =  $_REQUEST["jornada"];   if($jornada==""){      $respuesta = json_decode(readJornadaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readJornada($jornada));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
