<?php   require_once './../../../libs/saewsclient/known/functions.php';   $malla =  $_REQUEST["malla"];   if($malla==""){      $respuesta = json_decode(readMallaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readMalla($malla));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
