<?php   require_once './../../../libs/saewsclient/known/functions.php';   $matricula =  $_REQUEST["matricula"];   if($matricula==""){      $respuesta = json_decode(readMatriculaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readMatricula($matricula));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
