<?php   require_once './../../../libs/saewsclient/known/functions.php';   $modalidad =  $_REQUEST["modalidad"];   if($modalidad==""){      $respuesta = json_decode(readModalidadAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readModalidad($modalidad));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
