<?php   require_once './../../../libs/saewsclient/known/functions.php';   $motivosalida =  $_REQUEST["motivosalida"];   if($motivosalida==""){      $respuesta = json_decode(readMotivoSalidaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readMotivoSalida($motivosalida));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
