<?php   require_once './../../../libs/saewsclient/known/functions.php';   $niveltitulo =  $_REQUEST["niveltitulo"];   if($niveltitulo==""){      $respuesta = json_decode(readNivelTituloAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readNivelTitulo($niveltitulo));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
