<?php   require_once './../../../libs/saewsclient/known/functions.php';   $notas =  $_REQUEST["notas"];   if($notas==""){      $respuesta = json_decode(readNotasAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readNotas($notas));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
