<?php   require_once './../../../libs/saewsclient/known/functions.php';   $ocupacion =  $_REQUEST["ocupacion"];   if($ocupacion==""){      $respuesta = json_decode(readOcupacionAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readOcupacion($ocupacion));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
