<?php   require_once './../../../libs/saewsclient/known/functions.php';   $paralelo =  $_REQUEST["paralelo"];   if($paralelo==""){      $respuesta = json_decode(readParaleloAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readParalelo($paralelo));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
