<?php   require_once './../../../libs/saewsclient/known/functions.php';   $periodoacademico =  $_REQUEST["periodoacademico"];   if($periodoacademico==""){      $respuesta = json_decode(readPeriodoAcademicoAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readPeriodoAcademico($periodoacademico));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
