<?php   require_once './../../../libs/saewsclient/known/functions.php';   $persona =  $_REQUEST["persona"];   if($persona==""){      $respuesta = json_decode(readPersonaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readPersona($persona));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
