<?php   require_once './../../../libs/saewsclient/known/functions.php';   $ponderacion =  $_REQUEST["ponderacion"];   if($ponderacion==""){      $respuesta = json_decode(readPonderacionAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readPonderacion($ponderacion));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
