<?php   require_once './../../../libs/saewsclient/known/functions.php';   $requisito =  $_REQUEST["requisito"];   if($requisito==""){      $respuesta = json_decode(readRequisitoAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readRequisito($requisito));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
