<?php   require_once './../../../libs/saewsclient/known/functions.php';   $solicitudmatricula =  $_REQUEST["solicitudmatricula"];   if($solicitudmatricula==""){      $respuesta = json_decode(readSolicitudMatriculaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readSolicitudMatricula($solicitudmatricula));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
