<?php   require_once './../../../libs/saewsclient/known/functions.php';   $tipoaula =  $_REQUEST["tipoaula"];   if($tipoaula==""){      $respuesta = json_decode(readTipoAulaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readTipoAula($tipoaula));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
