<?php   require_once './../../../libs/saewsclient/known/functions.php';   $tipodiscapacidad =  $_REQUEST["tipodiscapacidad"];   if($tipodiscapacidad==""){      $respuesta = json_decode(readTipoDiscapacidadAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readTipoDiscapacidad($tipodiscapacidad));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
