<?php   require_once './../../../libs/saewsclient/known/functions.php';   $tipoeducacioncontinua =  $_REQUEST["tipoeducacioncontinua"];   if($tipoeducacioncontinua==""){      $respuesta = json_decode(readTipoEducacionContinuaAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readTipoEducacionContinua($tipoeducacioncontinua));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
