<?php   require_once './../../../libs/saewsclient/known/functions.php';   $tiporequisito =  $_REQUEST["tiporequisito"];   if($tiporequisito==""){      $respuesta = json_decode(readTipoRequisitoAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readTipoRequisito($tiporequisito));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
