<?php   require_once './../../../libs/saewsclient/known/functions.php';   $tiposangre =  $_REQUEST["tiposangre"];   if($tiposangre==""){      $respuesta = json_decode(readTipoSangreAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readTipoSangre($tiposangre));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
