<?php   require_once './../../../libs/saewsclient/known/functions.php';   $titulo =  $_REQUEST["titulo"];   if($titulo==""){      $respuesta = json_decode(readTituloAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readTitulo($titulo));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
