<?php   require_once './../../../libs/saewsclient/known/functions.php';   $ubicacion =  $_REQUEST["ubicacion"];   if($ubicacion==""){      $respuesta = json_decode(readUbicacionAll());      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }else{      $respuesta = json_decode(readUbicacion($ubicacion));      $array = [];      foreach($respuesta->results as $fila){         array_push($array,$fila);      }      echo json_encode($array);   }?>
